import{_ as d,c as e,a1 as a,o as r}from"./chunks/framework.DWyZKnlI.js";const p=JSON.parse('{"title":"少儿守护","description":"","frontmatter":{},"headers":[],"relativePath":"guide/guard.md","filePath":"guide/guard.md"}'),o={name:"guide/guard.md"};function c(i,t,h,n,l,s){return r(),e("div",null,t[0]||(t[0]=[a('<h1 id="少儿守护" tabindex="-1">少儿守护 <a class="header-anchor" href="#少儿守护" aria-label="Permalink to &quot;少儿守护&quot;">​</a></h1><h2 id="守护模式" tabindex="-1">守护模式 <a class="header-anchor" href="#守护模式" aria-label="Permalink to &quot;守护模式&quot;">​</a></h2><p>少儿守护有2种运行模式：<code>常规模式</code>、<code>严格模式</code>，满足不同的用户需求，模式对比如下：</p><table tabindex="0"><thead><tr><th>功能</th><th>常规模式</th><th>严格模式</th></tr></thead><tbody><tr><td>防沉迷</td><td>支持</td><td>支持</td></tr><tr><td>限制使用时间</td><td>支持</td><td>支持</td></tr><tr><td>听模式</td><td><code>不支持</code></td><td>支持</td></tr><tr><td>限制APP</td><td><code>不支持</code></td><td>支持</td></tr><tr><td>第三方应用</td><td>全部可用</td><td><code>抖音</code></td></tr><tr><td>保护视力</td><td>支持</td><td>支持</td></tr><tr><td>儿童识字</td><td><code>不支持</code></td><td>支持</td></tr><tr><td>语音搜索</td><td><code>不支持</code></td><td>支持</td></tr><tr><td>资源中心</td><td><code>不支持</code></td><td>支持</td></tr><tr><td>年龄推荐</td><td>6～10岁</td><td>3～8岁</td></tr></tbody></table><h3 id="常规模式" tabindex="-1">常规模式 <a class="header-anchor" href="#常规模式" aria-label="Permalink to &quot;常规模式&quot;">​</a></h3><p>常规模式是一种比较宽松的模式，允许用户操作手机里面的所有APP，要限制使用APP可以使用安卓自带的<code>应用锁</code>功能给应用上锁。次模式可以具备防沉迷和视力保护功能，当使用手机时间达到规定的时长后，在不知道守护密码或者指纹的时候，用户将无法继续使用手机；当眼睛距离屏幕太近时，手机屏幕会被遮挡，对用户进行提醒，起到保护眼睛的目的。</p><h3 id="严格模式" tabindex="-1">严格模式 <a class="header-anchor" href="#严格模式" aria-label="Permalink to &quot;严格模式&quot;">​</a></h3><p>严格模式采用手机的无障碍模式操作抖音，用户不能直接操作手机上面的其他应用和桌面，只能通过少儿守护界面和语音发送操作指令，支持搜索、切换下一集、播放、暂停等指令；另外用户可通过资源中心查看少儿比较喜欢的音视频资源，不认识字的少儿可通过语音进行抖音搜索，在搜索的过程中引导用户学习认字，特别适合幼小衔接的小朋友使用。该模式同样具防沉迷（限时）和视力保护（距离检测告警）功能，并且支持只听不可看的场景，极大保护视力。</p>',8)]))}const b=d(o,[["render",c]]);export{p as __pageData,b as default};
